import React from "react";

function ContactForm() {
	const [contact, setContact] = React.useState({
		name: "",
		email: "",
		phone: "",
		type: "personal",
	});

	const { name, email, phone, type } = contact;
	const onChange = (e) =>
		setContact({ ...contact, [e.target.name]: e.target.value });

	return (
		<>
			<form>
				<h2 className="text-primary">Add A New Contact</h2>
				<input
					type="text"
					placeholder="name"
					name="name"
					value={name}
					onChange={onChange}
				/>
				<input
					type="email"
					placeholder="Email Address"
					name="email"
					value={email}
					onChange={onChange}
				/>
				<input
					type="text"
					placeholder="Phone Number"
					name="phone"
					value={phone}
					onChange={onChange}
				/>
				<h5>Contact Type</h5>
				<input
					type="radio"
					name="type"
					value="personal"
					ckecked={tpe === "personal"}
				/>
			</form>
		</>
	);
}

export default ContactForm;
